import "./App.module.scss";
import Square from "./components/Square";

const numberOfFields = 5;

const matrix = Array.from({ length: numberOfFields }, (_, i) =>
  Array.from({ length: numberOfFields }, (_, i) => ({
    // id: i + (Math.random() * 100).toString(),
    // value: Math.floor(Math.random() * 10),
  }))
);

function App() {
  const onMouseHandler = (e) => {
    console.log(e.target.className);
    if (e.target.className.includes("hovered")) {
      e.target.className = e.target.className.split(" ")[0];
    } else {
      e.target.className = `${e.target.className} hovered`;
    }
  };
  return (
    <div className={style.app}>
      <div className={style.container}>
        <div className={style.matrixContainer} onMouseOver={onMouseHandler}>
          <h3>Dropdown</h3>
          {matrix.map((lines, indexR) => {
            return (
              <div className={style.matrixRow} key={indexR}>
                {lines.map((line, indexT) => (
                  <Square
                    indexR={indexR}
                    indexT={indexT}
                    line={line}
                    key={indexT}
                  />
                ))}
              </div>
            );
          })}
        </div>
        <div className={style.hoversContainer}>
          <h3>Hover squares</h3>
          <div></div>
        </div>
      </div>
    </div>
  );
}

export default App;
