import React, {useState} from 'react'

const Square = ({indexT, line, indexRow}) => {
  const [backToggle, setBackToggle] = useState(false)

  const onMouseHandler = () => {
    setBackToggle(prev => !prev)
  }
  console.log(indexRow, indexT);


  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        border: "1px solid grey",
        width: "30px",
        height: "30px",
        backgroundColor: backToggle ? 'black' : 'white'
      }}
      onMouseOver={onMouseHandler}
      key={indexT}
      id={line.id}
    >
      {line.value}
    </div>
  )
}

export default Square
