import "./App.scss";
import Square from "./components/Square";

const numberOfFields = 5;

const matrix = Array.from({ length: numberOfFields }, (_, i) =>
  Array.from({ length: numberOfFields }, (_, i) => ({
    // id: i + (Math.random() * 100).toString(),
    // value: Math.floor(Math.random() * 10),
  }))
);

function App() {
  return (
    <div className="App">
      <h1>Hello matrix</h1>
      <div
        className="container"
        // onMouseOver={(e) => console.log(e.target)}
      >
        <div
          style={{
            display: "flex",
            flexDirection: "column",
          }}
        >
          <h3>Dropdown</h3>
          {matrix.map((lines, indexR) => {
            return (
              <div
                style={{
                  display: "flex",
                }}
                key={indexR}
              >
                {lines.map((line, indexT) => (
                  <Square
                    indexR={indexR}
                    indexT={indexT}
                    line={line}
                    key={indexT}
                  />
                ))}
              </div>
            );
          })}
        </div>
        <div>
          <h3>Hover squares</h3>
          <div></div>
        </div>
      </div>
    </div>
  );
}

export default App;
