import "./App.module.scss";
import Square from "./components/Square";

const numberOfFields = 5;

const matrix = Array.from({ length: numberOfFields }, (_, i) =>
  Array.from({ length: numberOfFields }, (_, i) => ({
    // id: i + (Math.random() * 100).toString(),
    // value: Math.floor(Math.random() * 10),
  }))
);

function App() {
  return (
    <div className="app">
      <div className="container">
        <div className="matrixContainer">
          <h3>Dropdown</h3>
          {matrix.map((lines, indexR) => {
            return (
              <div
                className="matrixRow"
                key={indexR}
                onMouseOver={(e) => console.log(e.target.className)}
              >
                {lines.map((line, indexT) => (
                  <Square
                    indexR={indexR}
                    indexT={indexT}
                    line={line}
                    key={indexT}
                  />
                ))}
              </div>
            );
          })}
        </div>
        <div className="hoversContainer">
          <h3>Hover squares</h3>
          <div></div>
        </div>
      </div>
    </div>
  );
}

export default App;
