import React, {useState, useMemo} from 'react'

const Square = ({indexT, line, indexR}) => {
  const [backToggle, setBackToggle] = useState(false)

  const onMouseHandler = () => {
    setBackToggle(prev => !prev)
  }

  const {rowNum, colNum} = useMemo(() => {
    return {
      rowNum: indexT + 1,
      colNum: indexR + 1,
    }
  }, [indexT, indexR ])


  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        border: "1px solid grey",
        width: "70px",
        height: "70px",
        backgroundColor: backToggle ? 'black' : 'white'
      }}
      onMouseOver={onMouseHandler}
      key={indexT}
      id={line.id}
    >
      {`row ${rowNum} col ${colNum}`}
    </div>
  )
}

export default Square
