import { useMemo } from "react";
import "./App.scss";
import Square from "./components/Square";

const numberOfFields = 5;

const matrix = Array.from({ length: numberOfFields }, (_, i) =>
  Array.from({ length: numberOfFields }, (_, i) => ({
    // id: i + (Math.random() * 100).toString(),
    // value: Math.floor(Math.random() * 10),
  }))
);

function App() {
  const onHoverHandler = (e) => {
    console.log(e.target.id);
  };

  console.log(matrix);

  return (
    <div className="App">
      <h1>Hello matrix</h1>

      <div
        style={{
          display: "flex",
        }}
      >
        {matrix.map((lines, indexR) => {
          return (
            <div key={indexR}>
              {lines.map((line, indexT) => (
                <Square
                  indexRow={indexR}
                  indexT={indexT}
                  line={line}
                  key={indexT}
                />
              ))}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
