import "./App.scss";

const numberOfFields = 15;

const matrix = Array.from({ length: numberOfFields }, (_, i) =>
  Array.from({ length: numberOfFields }, (_, i) => ({
    id: i + (Math.random() * 100).toString(),
    // value: Math.floor(Math.random() * 10),
  }))
);

const onHoverHandler = (e) => {
  console.log(e.target.value);
};

function App() {
  return (
    <div className="App">
      <h1>Hello matrix</h1>

      <div
        style={{
          display: "flex",
        }}
      >
        {matrix.map((lines, index) => {
          return (
            <div key={index}>
              {lines.map((line, index) => (
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    border: "1px solid grey",
                    width: "30px",
                    height: "30px",
                  }}
                  onMouseOver={onHoverHandler}
                  key={index}
                >
                  {line.value}
                </div>
              ))}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
