import React, { useEffect, useState } from "react";
import ModeService from "./API/ModeService";
import "./App.scss";
import Square from "./components/Square";
import Select from "./components/UI/Select";

const numberOfFields = 6;

const matrix = Array.from({ length: numberOfFields }, (_, i) =>
  Array.from({ length: numberOfFields })
);

const App = () => {
  const [modes, setModes] = useState([]);
  const [mode, setMode] = useState(5);
  const [hoveredItems, setHoveredItems] = useState([]);

  const matrixGeterator = (numberOfFields) => {
    const matrix = Array.from({ length: numberOfFields }, (_, i) =>
      Array.from({ length: numberOfFields })
    );
    return matrix;
  };

  const fetchFields = async () => {
    try {
      const response = await ModeService.getAll();
      setModes(response);
    } catch (e) {
      console.log(e.message);
    }
  };

  const onStartHandler = () => {
    setMode(mode);
    matrixGeterator(mode);
  };

  const onMouseHandler = (e) => {
    if (e.target.className.includes("hovered")) {
      e.target.className = e.target.className.split(" ")[0];
      setHoveredItems([...hoveredItems.filter((item) => item !== e.target.id)]);
    } else if (e.target.id) {
      e.target.className = `${e.target.className} hovered`;
      setHoveredItems((prev) => [...prev, e.target.id]);
    }
  };

  useEffect(() => {
    fetchFields();
  }, []);

  return (
    <div className="app">
      <div className="container">
        <div className="matrixContainer" onMouseOver={onMouseHandler}>
          <h3>Dropdown</h3>
          <div>
            <Select
              defaultValue="Pick mode"
              options={modes}
              value={mode}
              onChange={(value) => setMode(Number(value))}
            />
            <button onClick={onStartHandler}>START</button>
          </div>
          {matrix.map((lines, indexR) => {
            return (
              <div className="matrixRow" key={indexR}>
                {lines.map((line, indexT) => (
                  <Square indexR={indexR} indexT={indexT} key={indexT} />
                ))}
              </div>
            );
          })}
        </div>
        <div className="hoversContainer">
          <h3>Hover squares</h3>
          <ul>
            {hoveredItems.map((item) => (
              <li key={item}>{item}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default App;
